import React, { Component } from 'react';
import { render } from 'react-dom'
import profilImg from '../Assets/profile.png';
import devImg from "../Assets/projet.gif";
import { Link } from 'react-router-dom'

class Sidebar extends Component {
    constructor(props) {
        super(props);

        this.sidebarData = props.sidebarData;
    }

    handleClick = (event) => {
        const {href} = event.target;
        console.log("Jai cliquÃ© sur ", href);
        const parseIndex = href.split('projet')[1];
        const result = this.state.projectData[parseIndex];
        console.log(result);
        this.setState(state => ({projectResult:result}) );
        event.preventDefault();
    };

    render() {
        return (
            <nav className={"navbar navbar-expand-lg navbar-dark "+ (window.location.hash == '#projects' ? 'bg-dark' : 'bg-primary') +" fixed-top"} id="sideNav">
                <a className="navbar-brand js-scroll-trigger" href="#page-top">
                    <span className="d-block d-lg-none">{this.sidebarData.firstName} {this.sidebarData.lastName}</span>
                    <span className="d-none d-lg-block">
                        <img className="img-fluid img-profile rounded-circle mx-auto mb-2" src={(window.location.hash == '#projects' ? devImg :profilImg )} alt="me"></img>
                    </span>
                </a>

                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                        <li className="nav-item">
                            <a className="nav-link js-scroll-trigger" href="#about">A propos de moi</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link js-scroll-trigger" href="#experience">ExpÃ©riences</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link js-scroll-trigger" href="#education">Formations</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link js-scroll-trigger" href="#skills">CompÃ©tences</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link js-scroll-trigger" href="#interests">Centre d'interet</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link js-scroll-trigger" href="#salesforce">Salesforce</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link js-scroll-trigger" href="#awards">Certifications</a>
                        </li>
                        <li className="nav-item dropdown">
                            <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button"
                               data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                Dropdown
                            </a>
                            <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                                { this.state.projectData.map(
                                    (item, index) =>
                                        <a className="nav-link js-scroll-trigger dropdown-item" href={"projet"} onClick={this.handleClick}>{item.Name}</a>
                                )}
                            </div>
                        </li>
                        <Link to="/projet">
                            <button className="btn btn-secondary" variant="outlined">
                                Mes rÃ©alisations
                            </button>
                        </Link>
                    </ul>
                </div>
            </nav>
        );
    }
}

export default Sidebar;
