import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAward } from "@fortawesome/free-solid-svg-icons";


class Awards extends Component {
    constructor(props) {
        super(props);

        this.awards = props.awards;
    }

    render() {
        return(
            <section className="resume-section p-3 p-lg-5 d-flex align-items-center" id="awards">
                <div className="w-100">
                    <h2 className="mb-5">Certifications üèÖ</h2>
                    <ul className="fa-ul mb-0">
                        {
                            this.awards.map((data, index) => (
                                <li key={index}>
                                    <FontAwesomeIcon icon={faAward} color="#ffc107" />
                                    <span className="ml-2"> {data.awardDetail} - {data.awardTitle} - {data.awardOrg}</span>
                                </li>
                            ))
                        }
                    </ul>
                </div>
            </section>
        );
    }
}

export default Awards;
